function y=mat_fun(X,Q,N);
T_=X(:,1); X_I=X(:,2:size(X,2)); X_T=X_I(size(X_I,1),:);
J_norm=[1000,1000];
T=0.054;                   
T1=1.51;                   
E=0.041;                   
k=0.0016;                  
a11=0.88;                  
a13=341.9;                 
a15=0.05;                  
b11=a11;                   
b13=a13;                   
b15=a15;                   
teta=0.35;                 
gama=0.7854;               
krp=0.077;                 
Kdg=2.3;                   
v=1000;                    
Mz=30;                     
My=30;                     
Jmax =[2                   
2                          
